import React, { useState, useEffect } from 'react';
import logUser from './logUser.js';
import axios from 'axios';
import Texts from '../ChatBox/frontend/Texts.jsx';
import AppliedApartments from '../Agent/AppliedApartments.jsx';

const AgentPortal = (props) => {
  // let admin = {
  //   name: 'laura90',
  //   email: 'laura90@gmail.com',
  //   role: 'agent'
  // }

  const [chatHist, setChatHist] = useState([]);
  const [chatIdx, setChatIdx] = useState(null);
  const [texts, setTexts] = useState(false);
  const [firstRender, setFirstRender] = useState(false);
  const [chatId, setChatId] = useState(null);
  const [resetId, setResetId] = useState(false);

  useEffect(() => {
    console.log('hit', props.admin)
    if (props.admin.hasOwnProperty('email')) {
      console.log('hit2', props.admin)
    return axios
      .get(`/msg/agent`, {
        params: {
          agentEmail: props.admin.email,
        },
      })
      .then(({ data }) => {
        console.log('here', data)
        setChatHist(data);
      });
    }
  }, [resetId, props.admin]);

  useEffect(() => {
    if (!firstRender) {
      console.log("didn't run");
      setFirstRender(true);
      return;
    }
    console.log('run');
    setChatId(chatHist[chatIdx].chatId);
    setTexts(true);
  }, [resetId]);

  //////////////////////////////////////Keep socket connection//////////////////////////
  // useEffect(() => {
  //   if (!inRoom) {
  //     setInRoom(true)
  //     for (let i = 0; i < chatHist.length; i++) {
  //       socket.emit('join room', {room: chatHist[i].chatId});
  //     }
  //   }
  //   // return () => {
  //   //   socket.emit('leave room', {
  //   //     room: props.chatId
  //   //   })
  //   // }
  // }, [chatHist])

  const exitChat = () => {
    setTexts(false);
  };

  const updateConvo = (messageObj, chatRoomId) => {
    if (chatRoomId === null) {
      return;
    }

    let outdatedChat = [...chatHist];
    for (let i = 0; i < outdatedChat.length; i++) {
      if (outdatedChat[i].chatId === chatRoomId) {
        let chatRoom = outdatedChat[i];
        chatRoom.messages.push(messageObj);
        setChatHist(outdatedChat);
      }
    }
  };

  return (
    <div>
      <div
        style={{
          width: '475px',
          height: '515px',
          border: '3px solid #6f6389',
          display: 'inline-block',
          position: 'fixed',
          backgroundColor: '#fff',
          top: '155px',
          // left: '10%',
          left: '1%',
          padding: '25px',
          overflow: 'scroll'
        }}>
        <h2 style={{
          textAlign: 'center',
          color: '#6f6389'
        }}>Property Inquiries</h2>
        <div>
          <div style={{marginBottom: "10px"}}>
            <div style={{
              display: 'inline-block',
              width: '33.3%',
              color: '#6f6389'
            }}>From</div>
            <div style={{
              display: 'inline-block',
              width: '33.3%',
              color: '#6f6389'
            }}>Address</div>
            <div style={{
              display: 'inline-block',
              width: '33.3%',
              position:'relative',
              top:'11px'
            }}>
              <img
                width='25'
                height='30'
                src='data:image/svg+xml;base64,'
              />
            </div>
          </div>
          <div style={{marginBottom: "10px"}}>
            {chatHist.map((inQs, idx) => (
              <React.Fragment>
                {(() => {
                  if (inQs.messages.length > 0) {
                    return (
                      <div
                        style={{ maxHeight: '50px', overflow: 'hidden' }}
                        key={idx}
                        onClick={() => {
                          setChatIdx(idx);
                          setResetId(!resetId);
                        }}
                      >
                        <div
                          style={{
                            display: 'inline-block',
                            width: '33.3%',
                            verticalAlign: 'top',
                            maxHeight: '40px',
                            overflowWrap: 'anywhere',
                            cursor: 'pointer'
                          }}>
                          {inQs.userName}
                        </div>
                        <div
                          style={{
                            display: 'inline-block',
                            width: '33.3%',
                            verticalAlign: 'top',
                            maxHeight: '40px',
                            overflowWrap: 'anywhere',
                            cursor: 'pointer'
                          }}>
                          {inQs.address}
                        </div>
                        <div
                          style={{
                            display: 'inline-block',
                            width: '33.3%',
                            verticalAlign: 'top',
                            maxHeight: '40px',
                            overflowWrap: 'anywhere',
                            cursor: 'pointer'
                          }}>
                          {inQs.messages[inQs.messages.length - 1].message}
                        </div>
                      </div>
                    );
                  }
                })()}
              </React.Fragment>
            ))}
          </div>
        </div>
      </div>
      <div
        style={{
          width: '475px',
          height: '515px',
          border: '3px solid #6f6389',
          display: 'inline-block',
          position: 'fixed',
          backgroundColor: '#fff',
          top: '155px',
          // right: '10%',
          right: '1%',
          padding: '25px',
          overflow: 'scroll'
        }}>
        <h2 style={{
          textAlign: 'center',
          color: '#6f6389'
        }}>Document Upload</h2>
        <AppliedApartments />
      </div>
      {texts ? (
        <Texts
          chatBox={chatHist[chatIdx]}
          exitChat={exitChat}
          updateConvo={updateConvo}
          chatId={chatId}
          loggedIn={props.admin}
        />
      ) : null}
    </div>
  );
};

export default AgentPortal;